    ) -&gt; (Span&lt;felt252&gt;, Span&lt;Span&lt;felt252&gt;&gt;);
    fn entity_ids(self: @T, model: felt252) -&gt; Span&lt;felt252&gt;;
    fn set_executor(ref self: T, contract_address: ContractAddress);
